// import { PayrailsCSE } from '@payrails/web-cse';
import * as jose from 'node-jose';
import * as i0 from "@angular/core";
export class EncryptionService {
    constructor(ngZone) {
        this.ngZone = ngZone;
    }
    async encryptData(data, publicKeyString) {
        try {
            let pemKey = this.formatPublicKeyAsPEM(publicKeyString);
            const key = await this.importPublicKey(pemKey);
            const buffer = new TextEncoder().encode(JSON.stringify(data));
            const encrypted = await jose.JWE.createEncrypt({
                format: 'compact',
                fields: {
                    alg: 'RSA-OAEP-256',
                    enc: 'A256CBC-HS512', // Content encryption algorithm
                },
            }, key)
                .update(buffer)
                .final();
            console.log("Encryption is", encrypted);
            return encrypted;
        }
        catch (error) {
            console.error('Encryption failed:', error);
            return '';
        }
    }
    async importPublicKey(pem) {
        const keystore = jose.JWK.createKeyStore();
        return keystore.add(pem, 'pem');
    }
    formatPublicKeyAsPEM(publicKey) {
        const pemHeader = '-----BEGIN PUBLIC KEY-----';
        const pemFooter = '-----END PUBLIC KEY-----';
        // Add newline characters if they are not present
        const normalizedKey = publicKey
            .replace(/^-----BEGIN PUBLIC KEY-----/, '')
            .replace(/-----END PUBLIC KEY-----$/, '')
            .trim();
        return `${pemHeader}\n${normalizedKey}\n${pemFooter}`;
    }
}
