import * as i0 from "@angular/core";
export class EncryptionService {
    constructor() { }
    async encryptCreditCard(creditCardModel, publicKey) {
        const cryptoKey = await this.importKey(publicKey);
        const encodedText = new TextEncoder().encode(JSON.stringify(creditCardModel));
        const encryptedData = await crypto.subtle.encrypt({
            name: 'RSA-OAEP',
        }, cryptoKey, encodedText);
        return btoa(String.fromCharCode(...new Uint8Array(encryptedData)));
    }
    async importKey(publicKey) {
        const keyBuffer = await crypto.subtle.importKey('spki', this.base64ToArrayBuffer(publicKey), {
            name: 'RSA-OAEP',
            hash: 'SHA-256',
        }, true, ['encrypt']);
        return keyBuffer;
    }
    base64ToArrayBuffer(base64) {
        const binaryString = window.atob(base64);
        const len = binaryString.length;
        const bytes = new Uint8Array(len);
        for (let i = 0; i < len; i++) {
            bytes[i] = binaryString.charCodeAt(i);
        }
        return bytes.buffer;
    }
}
