import { PayrailsCSE } from '@payrails/web-cse';
import * as jose from 'node-jose';
import * as i0 from "@angular/core";
export class EncryptionService {
    constructor(ngZone) {
        this.ngZone = ngZone;
    }
    async encryptData(data, publicKeyString) {
        try {
            const key = await this.importPublicKey(publicKeyString);
            const buffer = new TextEncoder().encode(JSON.stringify(data));
            const encrypted = await jose.JWE.createEncrypt({ format: 'compact' }, key)
                .update(buffer)
                .final();
            return encrypted;
        }
        catch (error) {
            console.error('Encryption failed:', error);
            return '';
        }
    }
    async importPublicKey(pem) {
        const keystore = jose.JWK.createKeyStore();
        return keystore.add(pem, 'pem');
    }
    async clientSideEncryption(creditCardModel) {
        let initResponse = {
            "version": "1.0.0",
            "data": ""
        };
        const cse = PayrailsCSE.init(initResponse);
        const tokenizedCard = await cse.tokenize(creditCardModel);
        console.log({ tokenizedCard });
    }
    /**
     * The following is in case we need to encrypt using encryption algorithm RSA-OAEP-256 and content encryption A256CBC-HS512 but without JWE - this should be done strictly from server side
     * @param creditCardModel
     * @param publicKey
     * @returns
     */
    async encryptCreditCard(creditCardModel, publicKey) {
        const cryptoKey = await this.importKey(publicKey);
        const encodedText = new TextEncoder().encode(JSON.stringify(creditCardModel));
        // Use NgZone.run to ensure that change detection is aware of the asynchronous operation
        return this.ngZone.run(async () => {
            const encryptedData = await crypto.subtle.encrypt({
                name: 'RSA-OAEP',
            }, cryptoKey, encodedText);
            return btoa(String.fromCharCode(...new Uint8Array(encryptedData)));
        });
    }
    // Import the public key for encryption
    async importKey(publicKey) {
        // Convert the base64-encoded public key to an ArrayBuffer
        const keyBuffer = await crypto.subtle.importKey('spki', this.base64ToArrayBuffer(publicKey), {
            name: 'RSA-OAEP',
            hash: 'SHA-256',
        }, true, ['encrypt']);
        return keyBuffer;
    }
    // Convert a base64-encoded string to an ArrayBuffer
    base64ToArrayBuffer(base64) {
        const binaryString = window.atob(base64);
        const len = binaryString.length;
        const bytes = new Array(len);
        for (let i = 0; i < len; i++) {
            bytes[i] = binaryString.charCodeAt(i);
        }
        return new Uint8Array(bytes).buffer;
    }
}
